import org.junit.Test;

import java.util.HashMap;
import java.util.Map;

/**
 * @author 小怪兽
 * @version 1.0
 * @since 2022-11-08
 */
public class JZII092 {

    private final char ONE = '1';
    private final char ZERO = '0';

    private Map<Integer, Integer> map = new HashMap<>();

    public int minFlipsMonoIncr(String s) {

        if (s.length() == 1) {
            return 0;
        }
        char first = s.charAt(0);
        if (first == ONE) {
            return Math.min(solve(1, s, ONE), solve(1, s, ZERO) + 1);
        } else {
            return solve(1, s, s.charAt(0));
        }
    }

    int solve(int index, String s, char ch) {
        if (index >= s.length()) {
            return 0;
        }

        if (index == s.length() - 1) {
            if (ch == ONE && s.charAt(index) == ONE) {
                return 0;
            }
            if (ch == ONE && s.charAt(index) == ONE) {
                return 1;
            }
        }
        if (map.containsKey(index)) {
            return map.get(index) + 1;
        }

        int res = 0;
        if (ch == ONE) {
            // 找到从index后第一个'0'的位置
            int i = index;
            for (; i < s.length() && s.charAt(i) == ONE; i++) {
            }
            if (i != s.length()) {
                res = 1 + solve(i + 1, s, ONE);
            }
        } else {
            char c = s.charAt(index);
            if (c == ZERO) {
                int a = solve(index + 1, s, ZERO);
                int b = solve(index + 1, s, ONE) + 1;
                res = Math.min(a, b);
                //map.put(index, res);
            } else {
                int a = 1 + solve(index + 1, s, ZERO);
                int b = solve(index + 1, s, ONE);
                res = Math.min(a, b);
                //map.put(index, res);
            }
        }
        map.put(index, res);
        return res;
    }

    @Test
    public void test47() {
        String s = "";
        int i = minFlipsMonoIncr(s);
        System.out.println(i);
    }
}
